mapper.select:
select if(count(*) >= 1, 1, 0)
from TB_MEMBER
where memID = #{id};

select if(count(*) >= 1, 1, 0)
from TB_MEMBER
where memEmail = #{email};

select if(count(*) >= 1, 1, 0)
from TB_MEMBER
where memID = #{memID} and memPW = #{memPW};

select count(*)
from TB_ADMIN
where adID = #{memID};

SELECT
*
FROM
TB_MEMBER
WHERE memID = #{memID}
AND
memName = #{memName}
AND
memEmail = #{memEmail}
